import React from "react";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { logIn } from "../store/slices/LoginSlice";
import { selectUsers } from "../store/slices/RegisterSlice";

const Login = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [loginedUser, setLoginedUser] = React.useState({
    email: "",
    password: "",
  });

  const users = useSelector(selectUsers);

  function checkValidUser(arr, obj) {
    const isValidUser = arr.find((item) => item.email === obj.email);
    if (isValidUser) {
      if (obj.password !== isValidUser.password) {
        alert("Enter corect date");
        return;
      } else {
        dispatch(logIn());
        navigate("/");
      }
    } else {
      alert("This user is't finded");
      return;
    }
  }

  const loginHandler = () => {
    checkValidUser(users, loginedUser);
  };

  return (
    <div>
      <input
        value={loginedUser.email}
        onChange={(e) =>
          setLoginedUser({ ...loginedUser, email: e.target.value })
        }
        placeholder="Enter Email"
      />
      <input
        value={loginedUser.password}
        onChange={(e) =>
          setLoginedUser({ ...loginedUser, password: e.target.value })
        }
        placeholder="Enter password"
      />
      <button onClick={loginHandler}>Login</button>
    </div>
  );
};

export default Login;
