import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  users: [],
};

const registerSlice = createSlice({
  name: "register",
  initialState,
  reducers: {
    addNewUser: (state, action) => {
      state.users.push(action.payload);
      // localStorage.setItem("users", JSON.stringify(state.users));
      // localStorage.setItem(
      //   "users",
      //   (state.users = [
      //     ...state.users,
      //     {
      //       id: new Date().toISOString(),
      //       email: action.payload.email,
      //       login: action.payload.login,
      //       password: action.payload.password,
      //     },
      //   ])
      // );
    },
    cheackUniqUsers: (state, action) => {
      const isUserExist = state.users.find(
        (item) => item.email === action.payload.email
      );

      if (isUserExist) {
        alert("Such a user is already registered");
        return false;
      } else {
        state.users = [...state.users, action.payload];
        const storedUsers = JSON.parse(localStorage.getItem("users"));

        localStorage.setItem(
          "users",
          JSON.stringify([...storedUsers, action.payload])
        );
        return true;
      }
    },
  },
});

export default registerSlice.reducer;

export const { addNewUser, cheackUniqUsers } = registerSlice.actions;

export const selectUsers = (state) => state.register.users;
